        width: this.state.width,
        height: this.state.height,
        align: this.state.align,
        pattern: this.state.pattern,
      }
    })
    .then(response => response.json())
    .then(response => {
      this.setState({saving: false});
      // TODO: Navigate to pattern route (/patterns/${response._id})
    })
    .catch(error => {
      alert('Error: ' + error);
      this.setState({saving: false});
    });
  }

